
# Problem link: https://rosalind.info/problems/ba1d/

def occur_pattern(pattern, text):
    ans = []
    p = len(pattern)
    t = len(text)
    i = 0
    while i < t-p+1:        
        pos = text.find(pattern,i)
        # print(pos)
        if(pos != -1):
            ans.append(str(pos))
            i = pos+1
            continue
        else:
            break
    return ' '.join(ans)

# pattern = 'ATAT'
# text = 'GATATATGCATATACTT'
pattern = 'ACTGACTAC'
text = ''
ans = occur_pattern(pattern,text)
f = open('ans.txt', 'a')
f.write(ans)
print(ans)
